from requests.exceptions import RequestException
from datetime import datetime, timedelta
import requests
import time
from typing import Iterator

class TokenAuthenticator:
    """
    A class that handles the authentication token for a given auth endpoint.
    
    """
    DEFAULT_TIMEOUT = 5
    REFRESH_INTERVAL_SECONDS = 250
    MAX_RETRIES = 5

    # Initializes the TokenAuthenticator with the provided auth endpoint
    def __init__(self, auth_endpoint: str, client_id: str, client_secret: str, telemetry_client):
        self.auth_endpoint = auth_endpoint
        self.client_id = client_id
        self.client_secret = client_secret
        self.telemetry_client = telemetry_client
        self.session = requests.Session()
        self.headers = {
            "Content-Type": "application/json"
        }
        self.params = {
            "client_id": self.client_id,
            "client_secret": self.client_secret
        }
        self.token = None
        self.expiry_timestamp = None
        self.num_retries = 0

    # Private Method that refreshes the token by sending a POST request to the auth endpoint and obtaining a new token if the current one has expired or is invalid
    def _refresh_token(self) -> str:
        try:
            response = self.session.post(self.auth_endpoint, headers=self.headers, params=self.params, timeout=self.DEFAULT_TIMEOUT)
            self.telemetry_client.track_event(f"WFS Auth API response headers: {response.headers}")
            self.telemetry_client.track_event(f"WFS Auth API returned a {response.status_code} status code.")
            
            response.raise_for_status()
            self.token = response.json().get("access_token")
            if self.token:
                self.expiry_timestamp = datetime.now() + timedelta(seconds=int(response.json().get("expires_in", 0)))
                return self.token
            else:
                #If the response from the auth endpoint doesn't contain an "access_token" field.
                raise ValueError("Response does not contain access_token field.")

        # If there's an error when sending the POST request to the auth endpoint
        except RequestException as e:
            self.num_retries += 1

            if self.num_retries <= self.MAX_RETRIES:
                self.telemetry_client.track_exception(f"Error fetching token {self.num_retries}: {e}")
                time.sleep(self.REFRESH_INTERVAL_SECONDS)
                return self._refresh_token()
            else:
                # If the maximum number of retries is reached when fetching the token.
                self.telemetry_client.track_exception(f"Error fetching token {self.num_retries}: {e}")
                raise RuntimeError("Maximum number of retries reached, giving up.")
        except json.decoder.JSONDecodeError as e:
            self.telemetry_client.track_exception(f"Error decoding response json {str(e)}")
            time.sleep(self.REFRESH_INTERVAL_SECONDS)
            return self._refresh_token()
            
    # Public method that returns the token. If the token is None or has expired, this method calls the _refresh_token method to obtain a new token.
    def get_auth_token(self) -> str:
        if self.token is None or self.expiry_timestamp is None or datetime.now() >= self.expiry_timestamp:
            self._refresh_token()
        return self.token
    
    def _token_generator(self) -> Iterator[str]:
        while True:
            yield self.get_auth_token()

    def get_auth_token_iterator(self) -> Iterator[str]:
        if self.token is None or self.expiry_timestamp is None or datetime.now() >= self.expiry_timestamp:
            self._refresh_token()

        return self._token_generator()
