import json
from pyspark.sql import DataFrame
from pyspark.sql.functions import col, lit
from pyspark.sql.types import StringType

class DataConfigsHelper:

    def __init__(self, data_configs: str, data_type: str):
        """
        Initializes a DataConfigsHelper object for interacting with a Data Config.

        Arguments:
            data_configs (str): Data config string to be parsed into json.
            data_type (str): Type of data config.
        Returns:
            None
        """
        data_config_str = getattr(data_configs, data_type)
        self.data_config_json = json.loads(data_config_str)

    def get_bronze_validation_df_rules_init_schema(self):
        return self.data_config_json["bronze"]["validationDataframeRules"]["initialSchema"]

    def get_bronze_validation_column_rules(self):
        return self.data_config_json["bronze"]["validationColumnRules"]

    def get_silver_finalized_schema(self):
        return self.data_config_json["silver"]["finalizedSchemaMapping"]

    def get_silver_validation_column_rules(self):
        return self.data_config_json["silver"]["validationColumnRules"]

    def get_silver_validation_df_rules_init_schema(self):
        return self.data_config_json["silver"]["validationDataframeRules"]["initialSchema"]

    def get_gold_finalized_schema(self):
        return self.data_config_json["gold"]["finalizedSchemaMapping"]

    def get_gold_validation_df_rules_init_schema(self):
        return self.data_config_json["gold"]["validationDataframeRules"]["initialSchema"]

    def get_gold_validation_column_rules(self):
        return self.data_config_json["gold"]["validationColumnRules"]

    def get_data_primary_key(self):
        return self.data_config_json["primaryKeyExpr"]

    def get_scientific_notation_conversion(self):
        return self.data_config_json["silver"]["scientificNotationConversionExpr"]
   
    def get_time_off_data(self):
        return self.data_config_json["silver"]["transformations"]

    def update_df_column_names(self, existing_df: DataFrame, schema_dict: dict): 
        new_columns = []
        for target_col_name, source_col_name in schema_dict.items():
            if source_col_name in existing_df.columns:
                new_columns.append(col(source_col_name).alias(target_col_name))
            else:
                new_columns.append(lit(None).cast(StringType()).alias(target_col_name))
        return existing_df.select(*new_columns)
