from pyspark.sql import SparkSession
from dependency_injector import containers, providers


class DependencyContainer(containers.DeclarativeContainer):
    """
    A dependency injection container for providing instances of various clients
    and services required for the application.
    """
    spark_session = providers.Singleton(SparkSession.builder.appName('hr-datalake-publish').getOrCreate)

    @providers.Singleton
    def datalake_client() -> DataLakeClient:
        """
        Provides an instance of the DataLakeClient.

        Returns:
            An instance of the DataLakeClient.
        """
        return DataLakeClient(linked_service_name, storage_account_name, spark_session)
     
    @providers.Singleton
    def key_vault_client() -> KeyVaultClient:
        """
        Provides an instance of the KeyVaultClient.

        Returns:
            An instance of the KeyVaultClient.
        """
        return KeyVaultClient('CartierKeyVault')

    @providers.Singleton
    def table_service_client(linked_service_name: str, storage_account_name: str) -> AzureTableClient:
        """
        Provides an instance of the AzureTableClient.

        Returns:
            An instance of the AzureTableClient.
        """

        account_url_table = f"https://{storage_account_name}.table.core.windows.net/"
        credential = ManagedIdentityCredential(linked_service_name)
        table_service_client = AzureTableServiceClient(account_url_table, credential)

        return AzureTableClient(table_service_client, spark_session)

    @providers.Singleton
    def telemetry_client(run_id: str, data_type: str, run_timestamp: str) -> TelemetryClient:
        """
        Provides an instance of the TelemetryClient.

        Returns:
            An instance of the TelemetryClient.
        """
        return TelemetryClient(instrumentation_key, run_id, data_type, run_timestamp)
