from azure.data.tables import TableClient, TableServiceClient 
from azure.core.credentials import TokenCredential, AccessToken 
import jwt 

class ManagedIdentityCredential(TokenCredential): 
    
    def __init__(self, linked_service_name: str): 
        self.linked_service_name = linked_service_name 

    def get_token(self, *scopes, **kwargs): 
        token = TokenLibrary.getConnectionString(self.linked_service_name) 
        expiry = self._get_token_expiry_time(token) 
        return AccessToken(token, expiry) 

    def _get_token_expiry_time(self,token): 
        """ 
            return expiry timestamp in sec 
        """ 
        
        if not token: 
            return 0 
            
        try: 
            decoded_token = jwt.decode(token, options={"verify_signature": False}) 
            expiry_time = decoded_token.get("exp") 
            
            return expiry_time 
            
        except Exception: 
            return 0
